/* Dialog content styling for review modal */
.dialog-content {
  max-height: 85vh;
  overflow-y: auto;
}

/* Ensure only one scrollbar in form content */
.scrollable-content {
  max-height: none;
  overflow-y: visible;
}

/* Section header styling */
.section-header {
