import { useState, useEffect, useRef } from 'react';
import { Html5Qrcode } from 'html5-qrcode';
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter, DialogClose } from "@/components/ui/dialog";
import { Camera, X } from 'lucide-react';
import { toast } from '@/hooks/use-toast';

interface BarcodeScannerProps {
  onCodeScanned: (code: string) => void;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function BarcodeScanner({ onCodeScanned, open, onOpenChange }: BarcodeScannerProps) {
  const [scanning, setScanning] = useState(false);
  const [permission, setPermission] = useState<boolean | null>(null);
  const [cameras, setCameras] = useState<Array<InputDeviceInfo>>([]);
  const [selectedCamera, setSelectedCamera] = useState<string | null>(null);
  const scannerRef = useRef<Html5Qrcode | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Check for camera permissions and available devices
  useEffect(() => {
    if (!open) return;

    const checkPermissions = async () => {
      try {
        // Request camera permission explicitly
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });

        // If we got here, permission was granted
        // We can close this stream as we'll reopen with the scanner
        stream.getTracks().forEach(track => track.stop());

        // Now get the list of cameras
        const devices = await navigator.mediaDevices.enumerateDevices();
        const videoDevices = devices.filter(device => device.kind === 'videoinput');
        setCameras(videoDevices as InputDeviceInfo[]);

        if (videoDevices.length > 0) {
          setSelectedCamera(videoDevices[0].deviceId);
          setPermission(true);

          // Auto-start scanner after getting permission
          setTimeout(() => {
            startScanner();
          }, 500);
        } else {
          setPermission(false);
          toast({
            title: "No cameras found",
            description: "Your device doesn't have any cameras available for scanning.",
            variant: "destructive"
          });
        }
      } catch (error) {
        console.error('Error checking camera permissions:', error);
        setPermission(false);
        toast({
          title: "Camera access denied",
          description: "Please grant camera permissions to use the barcode scanner.",
          variant: "destructive"
        });
      }
    };

    checkPermissions();

    return () => {
      stopScanner();
    };
  }, [open]);

  const startScanner = async () => {
    if (!selectedCamera || !containerRef.current) return;

    try {
      // Initialize scanner
      scannerRef.current = new Html5Qrcode('scanner-container');
      setScanning(true);

      await scannerRef.current.start(
        { deviceId: selectedCamera },
        {
          fps: 10,
          qrbox: { width: 250, height: 250 },
        },
        (decodedText) => {
          handleCodeScanned(decodedText);
        },
        (errorMessage) => {
          // Handle scan errors silently
          console.log(errorMessage);
        }
      );
    } catch (error) {
      console.error('Error starting scanner:', error);
      toast({
        title: "Scanner error",
        description: "Failed to start the barcode scanner. Please try again.",
        variant: "destructive"
      });
      setScanning(false);
    }
  };

  const stopScanner = async () => {
    if (scannerRef.current && scanning) {
      try {
        await scannerRef.current.stop();
        setScanning(false);
      } catch (error) {
        console.error('Error stopping scanner:', error);
      }
    }
  };

  const handleCodeScanned = (code: string) => {
    stopScanner();
    onCodeScanned(code);
    onOpenChange(false);
    toast({
      title: "Code scanned successfully",
      description: `Barcode detected: ${code}`,
    });
  };

  const handleDialogClose = () => {
    stopScanner();
    onOpenChange(false);
  };

  const handleCameraChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    if (scanning) {
      stopScanner();
    }
    setSelectedCamera(e.target.value);
  };

  return (
    <Dialog open={open} onOpenChange={handleDialogClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Scan Barcode or QR Code</DialogTitle>
          <DialogDescription>
            Position the barcode or QR code within the scanner frame.
          </DialogDescription>
        </DialogHeader>

        <div className="flex flex-col space-y-4">
          {permission === false && (
            <div className="p-4 bg-red-50 text-red-800 rounded-md">
              <p>Camera access is required to scan barcodes. Please grant camera permissions and try again.</p>
            </div>
          )}

          {permission === true && (
            <>
              {cameras.length > 1 && (
                <select 
                  className="w-full p-2 border rounded-md"
                  value={selectedCamera || ''}
                  onChange={handleCameraChange}
                >
                  {cameras.map(camera => (
                    <option key={camera.deviceId} value={camera.deviceId}>
                      {camera.label || `Camera ${camera.deviceId.substring(0, 5)}...`}
                    </option>
                  ))}
                </select>
              )}

              <div 
                ref={containerRef} 
                id="scanner-container"
                className="overflow-hidden rounded-lg aspect-square relative bg-black"
              >
                {!scanning && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/5">
                    <Camera className="w-16 h-16 text-muted-foreground/40" />
                  </div>
                )}
              </div>
            </>
          )}
        </div>

        <DialogFooter className="flex justify-between sm:justify-between">
          <DialogClose asChild>
            <Button type="button" variant="outline">
              Cancel
            </Button>
          </DialogClose>

          {permission === true && (
            scanning ? (
              <Button type="button" variant="destructive" onClick={stopScanner}>
                Stop Scanning
              </Button>
            ) : (
              <Button type="button" onClick={startScanner}>
                Start Scanning
              </Button>
            )
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/CollectionGrid.tsx.content:
import { Whiskey } from "@shared/schema";
import WhiskeyCard from "./WhiskeyCard";
import { Button } from "@/components/ui/button";
import { AlertCircle, PlusIcon } from "lucide-react";

interface CollectionGridProps {
  whiskeys: Whiskey[];
  isLoading: boolean;
  isError: boolean;
  onViewDetails: (whiskey: Whiskey) => void;
  onReview: (whiskey: Whiskey) => void;
  onEdit: (whiskey: Whiskey) => void; // Made non-optional to fix TypeScript error
  onAddNew: () => void;
}

const CollectionGrid = ({ 
  whiskeys, 
  isLoading, 
  isError, 
  onViewDetails, 
  onReview,
  onEdit,
  onAddNew
}: CollectionGridProps) => {
  // Loading state
  if (isLoading) {
    return (
      <div className="text-center py-16">
        <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-whiskey-400 border-r-transparent" role="status">
          <span className="sr-only">Loading...</span>
        </div>
        <p className="mt-4 text-gray-600">Loading your collection...</p>
      </div>
    );
  }

  // Error state
  if (isError) {
    return (
      <div className="bg-white rounded-lg shadow-sm p-8 text-center">
        <AlertCircle className="h-16 w-16 mx-auto text-red-500" />
        <h3 className="mt-4 text-lg font-medium text-gray-900">Error loading collection</h3>
        <p className="mt-1 text-gray-500">There was an error loading your whiskey collection. Please try again.</p>
      </div>
    );
  }

  // Empty state
  if (whiskeys.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow p-8 text-center">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
        </svg>
        <h3 className="mt-4 text-lg font-medium text-gray-900">No whiskeys found</h3>
        <p className="mt-1 text-gray-500">Start by adding your first whiskey or importing your collection.</p>
        <div className="mt-6">
          <Button 
            onClick={onAddNew}
            className="bg-whiskey-600 hover:bg-whiskey-500 text-white"
          >
            <PlusIcon className="h-4 w-4 mr-2" />
            Add Your First Whiskey
          </Button>
        </div>
      </div>
    );
  }

  // Collection grid
  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
      {whiskeys.map((whiskey) => (
        <WhiskeyCard
          key={whiskey.id}
          whiskey={whiskey}
          onViewDetails={onViewDetails}
          onReview={onReview}
          onEdit={onEdit}
        />
      ))}
    </div>
  );
};

export default CollectionGrid;

