import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { getQueryFn } from '@/lib/queryClient';
import { Whiskey, ReviewNote, User } from '@shared/schema';
import { Card, CardContent, CardFooter, CardHeader } from '@/components/ui/card';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Heart, MessageSquare, Share2 } from 'lucide-react';
import { Link } from 'wouter';
import { Button } from '@/components/ui/button';
import ReviewLikes from './ReviewLikes';

interface PublicReview {
  whiskey: Whiskey;
  review: ReviewNote;
  user: User;
  likes?: number;
  comments?: number;
  userHasLiked?: boolean;
}

const ReviewCard = ({
  whiskey,
  review,
  user,
  likes = 0,
  comments = 0,
  userHasLiked = false
}: PublicReview) => {
  // Calculate preview text (first 150 characters)
  const previewText = review.text.length > 150 
    ? `${review.text.substring(0, 150)}...` 
    : review.text;

  // Format date
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  // Get initials for avatar
  const getInitials = (username: string) => {
    return username.substring(0, 2).toUpperCase();
  };

  return (
    <Card className="h-full flex flex-col hover:shadow-md transition-shadow">
      <CardHeader className="pb-2 pt-4">
        <div className="flex justify-between items-start mb-2">
          <div className="flex items-center gap-2">
            <Avatar className="h-8 w-8">
              <AvatarFallback>{getInitials(user.username)}</AvatarFallback>
            </Avatar>
            <div>
              <p className="text-sm font-medium">{user.username}</p>
              <p className="text-xs text-muted-foreground">{formatDate(review.date)}</p>
            </div>
          </div>
          <Badge variant="outline">{whiskey.type}</Badge>
        </div>
        <h3 className="text-lg font-semibold line-clamp-1">{whiskey.name}</h3>
        <p className="text-sm text-muted-foreground line-clamp-1">
          {whiskey.distillery}
          {whiskey.age && ` • ${whiskey.age} years`}
          {whiskey.abv && ` • ${whiskey.abv}% ABV`}
        </p>
      </CardHeader>
      <CardContent className="flex-grow">
        <div className="flex justify-between items-start mb-3">
          <div className="text-2xl font-bold text-amber-600">{review.rating.toFixed(1)}</div>
        </div>
        <p className="text-sm line-clamp-4">{previewText}</p>
      </CardContent>
      <CardFooter className="flex justify-between border-t pt-4 gap-2">
        <ReviewLikes whiskey={whiskey} review={review} size="sm" />

        <Button 
          asChild
          variant="outline" 
          size="sm"
          className="h-7 px-2 text-xs"
        >
          <Link to={`/shared/${review.shareId}`}>
            <Share2 className="h-3 w-3 mr-1" /> 
            View
          </Link>
        </Button>
      </CardFooter>
    </Card>
  );
};

interface PublicReviewsGridProps {
  limit?: number;
  className?: string;
}

const PublicReviewsGrid = ({ limit = 6, className = '' }: PublicReviewsGridProps) => {
  const { data: reviews = [], isLoading, error } = useQuery<PublicReview[]>({
    queryKey: [`/api/reviews/public?limit=${limit}`],
    queryFn: getQueryFn({ on401: 'returnNull' }),
  });

  if (error) {
    return (
      <div className={`p-6 bg-red-50 text-red-700 rounded-md ${className}`}>
        Failed to load public reviews. Please try again later.
      </div>
    );
  }

  return (
    <div className={className}>
      {isLoading ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array(limit).fill(0).map((_, i) => (
            <Card key={i} className="h-96">
              <CardHeader className="pb-0">
                <div className="flex justify-between items-start mb-2">
                  <div className="flex items-center gap-2">
                    <Skeleton className="h-8 w-8 rounded-full" />
                    <div>
                      <Skeleton className="h-4 w-24 mb-1" />
                      <Skeleton className="h-3 w-16" />
                    </div>
                  </div>
                  <Skeleton className="h-6 w-20 rounded-full" />
                </div>
                <Skeleton className="h-6 w-4/5 mb-1 mt-2" />
                <Skeleton className="h-4 w-2/3" />
              </CardHeader>
              <CardContent className="pt-4">
                <div className="flex justify-between items-start mb-4">
                  <Skeleton className="h-8 w-10" />
                </div>
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-4/5 mb-2" />
                <Skeleton className="h-4 w-3/4" />
              </CardContent>
              <CardFooter className="border-t pt-4">
                <div className="flex justify-between w-full">
                  <Skeleton className="h-8 w-20" />
                  <Skeleton className="h-8 w-20" />
                </div>
              </CardFooter>
            </Card>
          ))}
        </div>
      ) : reviews.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {reviews.map((review, index) => (
            <ReviewCard
              key={`${review.whiskey.id}-${review.review.id}`}
              whiskey={review.whiskey}
              review={review.review}
              user={review.user}
              likes={review.likes}
              comments={review.comments}
              userHasLiked={review.userHasLiked}
            />
          ))}
        </div>
      ) : (
        <div className="flex flex-col items-center justify-center p-12 text-center bg-muted/20 rounded-lg">
          <h3 className="text-xl font-medium mb-2">No public reviews yet</h3>
          <p className="text-muted-foreground mb-6">
            Be the first to share your whiskey review with the community!
          </p>
        </div>
      )}
    </div>
  );
};

export default PublicReviewsGrid;

client/src/components/RadarChart.tsx.content:
import React, { useEffect, useRef } from 'react';

interface FlavorProfile {
  fruitFloral: number;
  sweet: number;
  spice: number;
  herbal: number;
  grain: number;
  oak: number;
}

interface RadarChartProps {
  flavorProfile: FlavorProfile;
  size?: number;
  className?: string;
}

export function RadarChart({ flavorProfile, size = 200, className = "" }: RadarChartProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Set canvas dimensions
    const canvasSize = size;
    canvas.width = canvasSize;
    canvas.height = canvasSize;

    // Define radar chart parameters
    const centerX = canvasSize / 2;
    const centerY = canvasSize / 2;
    const radius = (canvasSize / 2) * 0.8; // 80% of half canvas size

    // Convert flavor profile to array for easier processing
    const categories = ['fruitFloral', 'sweet', 'spice', 'herbal', 'grain', 'oak'];
    const values = categories.map(cat => flavorProfile[cat as keyof FlavorProfile] || 0);

    // Calculate the angle for each category (in radians)
    const angleStep = (Math.PI * 2) / categories.length;

    // Draw background grid
    ctx.strokeStyle = '#E8D9BD';
    ctx.fillStyle = 'rgba(232, 217, 189, 0.2)';

    // Draw grid levels (5 levels for 0-5 scale)
    for (let level = 1; level <= 5; level++) {
      const levelRadius = (radius / 5) * level;

      ctx.beginPath();
      for (let i = 0; i < categories.length; i++) {
        const angle = i * angleStep - Math.PI / 2; // Start from top (subtract PI/2)
        if (i === 0) {
          ctx.moveTo(centerX + levelRadius * Math.cos(angle), centerY + levelRadius * Math.sin(angle));
        } else {
          ctx.lineTo(centerX + levelRadius * Math.cos(angle), centerY + levelRadius * Math.sin(angle));
        }
      }
      ctx.closePath();
      ctx.stroke();
    }

    // Draw axes
    ctx.strokeStyle = '#D9C4A3';
    ctx.fillStyle = '#794E2F';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.font = `${Math.round(canvasSize/20)}px sans-serif`;

    for (let i = 0; i < categories.length; i++) {
      const angle = i * angleStep - Math.PI / 2; // Start from top (subtract PI/2)

      // Draw axis
      ctx.beginPath();
      ctx.moveTo(centerX, centerY);
      ctx.lineTo(centerX + radius * Math.cos(angle), centerY + radius * Math.sin(angle));
      ctx.stroke();

      // Draw label
      const labelDistance = radius * 1.1; // Place labels slightly outside the radar
      const labelX = centerX + labelDistance * Math.cos(angle);
      const labelY = centerY + labelDistance * Math.sin(angle);

      // Format the label for display
      let label;
      switch(categories[i]) {
        case 'fruitFloral':
          label = 'Fruit/Floral';
          break;
        case 'sweet':
          label = 'Sweet';
          break;
        case 'spice':
          label = 'Spice';
          break;
        case 'herbal':
          label = 'Herbal';
          break;
        case 'grain':
          label = 'Grain';
          break;
        case 'oak':
          label = 'Oak';
          break;
        default:
          // Capitalize first letter
          label = categories[i].charAt(0).toUpperCase() + categories[i].slice(1);
      }
      ctx.fillText(label, labelX, labelY);
    }

    // Draw data points and connect them
    ctx.strokeStyle = '#986A44';
    ctx.lineWidth = 2;
    ctx.fillStyle = 'rgba(152, 106, 68, 0.3)';

    ctx.beginPath();
    for (let i = 0; i < categories.length; i++) {
      const angle = i * angleStep - Math.PI / 2; // Start from top (subtract PI/2)
      const value = values[i];
      const scaledRadius = (radius / 5) * value; // Scale radius based on value (0-5)

      const pointX = centerX + scaledRadius * Math.cos(angle);
      const pointY = centerY + scaledRadius * Math.sin(angle);

      if (i === 0) {
        ctx.moveTo(pointX, pointY);
      } else {
        ctx.lineTo(pointX, pointY);
      }

      // Draw point
      ctx.fillStyle = '#986A44';
      ctx.beginPath();
      ctx.arc(pointX, pointY, 4, 0, Math.PI * 2);
      ctx.fill();
    }

    // Close and stroke the data polygon
    ctx.closePath();
    ctx.fillStyle = 'rgba(152, 106, 68, 0.3)';
    ctx.fill();
    ctx.stroke();

  }, [flavorProfile, size]);

  return (
    <div className={`radar-chart ${className}`}>
      <canvas 
        ref={canvasRef} 
        width={size} 
        height={size} 
        className="mx-auto"
      />
    </div>
  );
}

export default RadarChart;

client/src/components/ReviewComments.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { getQueryFn, apiRequest, queryClient } from '@/lib/queryClient';
import { useAuth } from '@/hooks/use-auth';
import { Whiskey, ReviewNote, ReviewComment } from '@shared/schema';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Form, FormField, FormItem, FormControl, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Skeleton } from '@/components/ui/skeleton';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { 
  MoreHorizontal, 
  Trash2, 
  Edit, 
  Send, 
  Check, 
  X
} from 'lucide-react';
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuTrigger 
} from '@/components/ui/dropdown-menu';
import { useToast } from '@/hooks/use-toast';

// Schema for comment creation/editing
const commentSchema = z.object({
  text: z.string().min(1, 'Comment cannot be empty').max(500, 'Comment is too long (max 500 characters)'),
});

type CommentFormValues = z.infer<typeof commentSchema>;

interface ReviewCommentsProps {
  whiskey: Whiskey;
  review: ReviewNote;
  className?: string;
}

const ReviewComments = ({ whiskey, review, className = '' }: ReviewCommentsProps) => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [editingCommentId, setEditingCommentId] = useState<number | null>(null);

  // Query to get comments
  const { 
    data: comments = [], 
    isLoading,
    error,
    refetch
  } = useQuery<ReviewComment[]>({
    queryKey: [`/api/whiskeys/${whiskey.id}/reviews/${review.id}/comments`],
    queryFn: getQueryFn({ on401: 'returnNull' }),
    enabled: !!whiskey.id && !!review.id,
  });

  // Form for adding a new comment
  const form = useForm<CommentFormValues>({
    resolver: zodResolver(commentSchema),
    defaultValues: {
      text: '',
    },
  });

  // Form for editing comments
  const editForm = useForm<CommentFormValues>({
    resolver: zodResolver(commentSchema),
    defaultValues: {
      text: '',
    },
  });

  // Add comment mutation
  const addCommentMutation = useMutation({
    mutationFn: async (data: CommentFormValues) => {
      const response = await apiRequest(
        'POST',
        `/api/whiskeys/${whiskey.id}/reviews/${review.id}/comments`,
        { text: data.text }
      );
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Comment added',
        description: 'Your comment has been posted.',
      });
      form.reset();
      refetch();
    },
    onError: (error) => {
      toast({
        title: 'Error',
        description: `Failed to add comment: ${error.message}`,
        variant: 'destructive',
      });
    },
  });

  // Edit comment mutation
  const editCommentMutation = useMutation({
    mutationFn: async ({ id, text }: { id: number; text: string }) => {
      const response = await apiRequest(
        'PUT',
        `/api/comments/${id}`,
        { text }
      );
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Comment updated',
        description: 'Your comment has been updated.',
      });
      setEditingCommentId(null);
      refetch();
    },
    onError: (error) => {
      toast({
        title: 'Error',
        description: `Failed to update comment: ${error.message}`,
        variant: 'destructive',
      });
    },
  });

  // Delete comment mutation
  const deleteCommentMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest(
        'DELETE',
        `/api/comments/${id}`
      );
      return response;
    },
    onSuccess: () => {
      toast({
        title: 'Comment deleted',
        description: 'Your comment has been removed.',
      });
      refetch();
    },
    onError: (error) => {
      toast({
        title: 'Error',
        description: `Failed to delete comment: ${error.message}`,
        variant: 'destructive',
      });
    },
  });

  // Handler for submitting a new comment
  const onSubmit = (values: CommentFormValues) => {
    if (!user) {
      toast({
        title: 'Authentication required',
        description: 'Please sign in to post comments.',
        variant: 'destructive',
      });
      return;
    }

    addCommentMutation.mutate(values);
  };

  // Handler for editing a comment
  const startEditing = (comment: ReviewComment) => {
    editForm.reset({ text: comment.text });
    setEditingCommentId(comment.id);
  };

  // Handler for saving an edited comment
  const saveEdit = (id: number) => {
    const { text } = editForm.getValues();
    editCommentMutation.mutate({ id, text });
  };

  // Handler for cancelling an edit
  const cancelEdit = () => {
    setEditingCommentId(null);
  };

  // Handler for deleting a comment
  const deleteComment = (id: number) => {
    if (confirm('Are you sure you want to delete this comment?')) {
      deleteCommentMutation.mutate(id);
    }
  };

  // Format date for display
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Get initials for avatar fallback
  const getInitials = (username: string) => {
    return username.substring(0, 2).toUpperCase();
  };

  if (error) {
    return (
      <div className={`${className} p-4 text-red-600 bg-red-50 rounded-lg`}>
        Failed to load comments. Please try again later.
      </div>
    );
  }

  return (
    <div className={`${className} space-y-6`}>
      <h3 className="text-xl font-semibold">Comments</h3>

      {/* Comment form */}
      {user && (
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="text"
              render={({ field }) => (
                <FormItem>
                  <div className="flex gap-2">
                    <Avatar className="h-10 w-10">
                      <AvatarFallback>{getInitials(user.username)}</AvatarFallback>
                    </Avatar>
                    <div className="flex-1 flex gap-2">
                      <FormControl>
                        <Input
                          placeholder="Add a comment..."
                          className="flex-1"
                          {...field}
                        />
                      </FormControl>
                      <Button 
                        type="submit" 
                        size="sm"
                        disabled={addCommentMutation.isPending}
                      >
                        {addCommentMutation.isPending ? (
                          <Skeleton className="h-4 w-4" />
                        ) : (
                          <Send className="h-4 w-4" />
                        )}
                      </Button>
                    </div>
                  </div>
                  <FormMessage />
                </FormItem>
              )}
            />
          </form>
        </Form>
      )}

      {/* Comments list */}
      <div className="space-y-4">
        {isLoading ? (
          // Loading skeletons
          Array(3).fill(0).map((_, i) => (
            <div key={i} className="flex gap-3">
              <Skeleton className="h-10 w-10 rounded-full" />
              <div className="flex-1">
                <Skeleton className="h-4 w-32 mb-2" />
                <Skeleton className="h-3 w-full mb-1" />
                <Skeleton className="h-3 w-4/5" />
              </div>
            </div>
          ))
        ) : comments.length > 0 ? (
          // Comments list
          comments.map((comment) => (
            <div key={comment.id} className="flex gap-3">
              <Avatar className="h-10 w-10">
                <AvatarFallback>
                  {getInitials(comment.username || 'User')}
                </AvatarFallback>
              </Avatar>

              <div className="flex-1">
                <div className="flex justify-between items-start">
                  <div>
                    <span className="font-medium text-sm">{comment.username}</span>
                    <span className="text-xs text-muted-foreground ml-2">
                      {formatDate(comment.createdAt)}
                    </span>
                  </div>

                  {/* Comment actions (edit/delete) */}
                  {user && user.id === comment.userId && (
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm" className="h-7 w-7 p-0">
                          <MoreHorizontal className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => startEditing(comment)}>
                          <Edit className="h-4 w-4 mr-2" />
                          Edit
                        </DropdownMenuItem>
                        <DropdownMenuItem 
                          onClick={() => deleteComment(comment.id)}
                          className="text-red-600"
                        >
                          <Trash2 className="h-4 w-4 mr-2" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  )}
                </div>

                {/* Comment content (normal or editing mode) */}
                {editingCommentId === comment.id ? (
                  <Form {...editForm}>
                    <form className="mt-1">
                      <FormField
                        control={editForm.control}
                        name="text"
                        render={({ field }) => (
                          <FormItem>
                            <div className="flex gap-2">
                              <FormControl>
                                <Input {...field} />
                              </FormControl>
                              <Button 
                                type="button" 
                                size="sm" 
                                variant="outline"
                                className="h-9 w-9 p-0"
                                onClick={() => saveEdit(comment.id)}
                                disabled={editCommentMutation.isPending}
                              >
                                <Check className="h-4 w-4" />
                              </Button>
                              <Button 
                                type="button" 
                                size="sm" 
                                variant="outline"
                                className="h-9 w-9 p-0"
                                onClick={cancelEdit}
                              >
                                <X className="h-4 w-4" />
                              </Button>
                            </div>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </form>
                  </Form>
                ) : (
                  <p className="text-sm mt-1">{comment.text}</p>
                )}
              </div>
            </div>
          ))
        ) : (
          // No comments
          <div className="text-center py-8 text-muted-foreground">
            <p>No comments yet.</p>
            {user && (
              <p className="text-sm mt-1">Be the first to share your thoughts!</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ReviewComments;

client/src/components/ReviewDetailPage.t.content:
import React from 'react';
import { Whiskey, ReviewNote } from '@shared/schema';
import {
  Card,
  CardContent,
} from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { StarRating } from '@/components/StarRating';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';

// Define a CSS class for section titles
const sectionTitleClass = "font-bold mb-2 text-gray-900";

interface ReviewDetailPageProps {
  whiskey?: Whiskey;
  review?: ReviewNote;
}

export function ReviewDetailPage({ whiskey, review }: ReviewDetailPageProps) {
  // Ensure both whiskey and review are defined before rendering
  if (!whiskey || !review) {
    return (
      <div className="bg-white rounded-lg shadow p-6 text-center">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">Missing Data</h2>
        <p className="text-gray-600 mb-6">Cannot display review details due to missing data.</p>
      </div>
    );
  }
  const renderMashBill = () => {
    if (whiskey.type !== 'Bourbon' || !whiskey.mashBill) return null;

    const mashBill = whiskey.mashBill
      .split(',')
      .map(item => item.trim())
      .filter(Boolean);

    if (mashBill.length === 0) return null;

    return (
      <TableRow>
        <TableCell className="font-medium bg-gray-200 text-gray-900">Mashbill</TableCell>
        <TableCell colSpan={2}>
          <Table>
            <TableBody>
              {mashBill.map((ingredient, index) => {
                // Parse ingredient - assume format like "Corn 75%" or just "Corn"
                const parts = ingredient.split(' ');
                const grain = parts[0];
                const percentage = parts.length > 1 ? parts[1] : '';

                return (
                  <TableRow key={index}>
                    <TableCell className="py-1 border font-medium">{grain}</TableCell>
                    <TableCell className="py-1 text-right border font-semibold text-gray-900">{percentage}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableCell>
      </TableRow>
    );
  };

  const calculateScores = () => {
    // Calculate scores based on the weighted scoring system:
    // Nose: 1.5x, Mouth Feel: 2.0x, Taste: 3.0x, Finish: 2.5x, Value: 1.0x
    let weightedTotal = 0;
    let scoresPresent = false;

    // Apply weights to each score
    if (review.noseScore) {
      weightedTotal += review.noseScore * 1.5;
      scoresPresent = true;
    }

    if (review.mouthfeelScore) {
      weightedTotal += review.mouthfeelScore * 2.0;
      scoresPresent = true;
    }

    if (review.tasteScore) {
      weightedTotal += review.tasteScore * 3.0;
      scoresPresent = true;
    }

    if (review.finishScore) {
      weightedTotal += review.finishScore * 2.5;
      scoresPresent = true;
    }

    if (review.valueScore) {
      weightedTotal += review.valueScore * 1.0;
      scoresPresent = true;
    }

    // If no detailed scores are present, return scores based on overall rating
    if (!scoresPresent) {
      const baseScore = review.rating * 10; // Convert 5-scale to 50-scale
      return {
        weightedTotal: baseScore,
        fiveStarScore: review.rating,
        finalScore: baseScore * 2
      };
    }

    // Calculate the 5-star score (weighted total divided by 10)
    const fiveStarScore = weightedTotal / 10;

    // Calculate the final score (weighted total multiplied by 2)
    const finalScore = weightedTotal * 2;

    return {
      weightedTotal,
      fiveStarScore,
      finalScore
    };
  };

  const renderScoreRow = (label: string, score?: number) => {
    if (!score) return null;

    return (
      <tr className="border-b border-gray-100">
        <td className="px-4 py-2 font-medium text-gray-800">{label}</td>
        <td className="px-4 py-2 flex items-center">
          <span className="mr-2 font-semibold text-gray-900">{score}</span>
          <StarRating rating={score} maxRating={5} />
        </td>
      </tr>
    );
  };

  const capitalizeFirstLetter = (text: string) => {
    return text.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const formatAromasList = (aromas: string[] | undefined) => {
    if (!aromas || aromas.length === 0) return '-';

    const formattedAromas = aromas.map(aroma => 
      aroma.split(',').map(part => capitalizeFirstLetter(part.trim())).join(', ')
    );

    return (
      <div className="flex flex-wrap gap-1 mt-1">
        {formattedAromas.map((aroma, index) => (
          <span 
            key={index} 
            className="bg-gray-200 text-gray-800 px-2 py-1 rounded-md text-sm font-medium"
          >
            {aroma}
          </span>
        ))}
      </div>
    );
  };

  const formatNotes = (notes: string | undefined) => {
    if (!notes) return '-';

    // Split by new lines and handle paragraphs
    const paragraphs = notes.split(/\n\s*\n|\r\n\s*\r\n/); // Split on empty lines

    if (paragraphs.length <= 1) {
      return notes; // Return as-is if no paragraphs detected
    }

    return (
      <div className="space-y-2">
        {paragraphs.map((paragraph, index) => (
          <p key={index} className="text-gray-800">
            {paragraph.trim()}
          </p>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-white rounded-lg shadow-lg max-w-5xl mx-auto">
      {/* Header with whiskey name and overall score */}
      <div className="bg-gray-800 text-white p-6 rounded-t-lg">
        <h1 className="text-3xl font-bold text-center">{whiskey.name}</h1>
        {whiskey.distillery && <p className="text-center text-gray-300 mt-1">{whiskey.distillery}</p>}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4">
        {/* Left column - Whiskey Details */}
        <div className="md:col-span-1">
          <h3 className="text-lg font-bold mb-2 text-gray-900 border-b border-gray-300 pb-2">Whiskey Details</h3>
          <Table>
            <TableBody>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Category</TableCell>
                <TableCell>{whiskey.type || '-'}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Company</TableCell>
                <TableCell>{whiskey.distillery || '-'}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Distillery</TableCell>
                <TableCell>{whiskey.distillery || '-'}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Release Date</TableCell>
                <TableCell>{whiskey.releaseDate ? new Date(whiskey.releaseDate).toLocaleDateString() : '-'}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Proof</TableCell>
                <TableCell>{whiskey.proof || (whiskey.abv ? Math.round(whiskey.abv * 2) : '-')}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Age</TableCell>
                <TableCell>{whiskey.age ? `${whiskey.age} years` : '-'}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">MSRP</TableCell>
                <TableCell>
                  {whiskey.msrp ? `$${whiskey.msrp.toFixed(2)}` : 
                   whiskey.price ? `$${whiskey.price.toFixed(2)}` : '-'}
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Paid</TableCell>
                <TableCell>
                  {whiskey.pricePaid ? `$${whiskey.pricePaid.toFixed(2)}` : 
                   whiskey.price ? `$${whiskey.price.toFixed(2)}` : '-'}
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell className="font-medium bg-gray-200 text-gray-900">Color</TableCell>
                <TableCell>{review.visualColor ? capitalizeFirstLetter(review.visualColor) : '-'}</TableCell>
              </TableRow>
              {renderMashBill()}
            </TableBody>
          </Table>
        </div>

        {/* Middle column - Score */}
        <div className="md:col-span-1 flex flex-col items-center justify-start">
          <div className="text-center">
            <div className="border-4 border-gray-800 rounded-lg p-6 w-32 h-32 flex flex-col items-center justify-center bg-gray-50">
              <span className="text-7xl font-bold text-gray-900">{Math.round(calculateScores().finalScore)}</span>
              <span className="text-xs mt-1 text-gray-700 font-medium">Final Score</span>
            </div>
            <div className="mt-3 text-md">
              <div className="text-gray-900 font-semibold">5★ Score: <span className="text-lg">{calculateScores().fiveStarScore.toFixed(1)}</span></div>
              <div className="text-gray-600 text-sm">Weighted Total: {Math.round(calculateScores().weightedTotal)}</div>
            </div>
          </div>

          <div className="mt-4 w-full">
            <h3 className="text-lg font-bold text-center mb-2 text-gray-900 border-b border-gray-300 pb-2">Component Scores</h3>
            <table className="w-full">
              <tbody>
                {renderScoreRow('Nose', review.noseScore)}
                {renderScoreRow('Texture', review.mouthfeelScore)}
                {renderScoreRow('Taste', review.tasteScore)}
                {renderScoreRow('Finish', review.finishScore)}
                {renderScoreRow('Value', review.valueScore)}
                {renderScoreRow('Overall', review.rating)}
              </tbody>
            </table>
          </div>
        </div>

        {/* Right column - Image */}
        <div className="md:col-span-1 flex flex-col">
          <h3 className="text-lg font-bold mb-2 text-gray-900 border-b border-gray-300 pb-2">Bottle Image</h3>
          <div className="flex justify-center flex-1">
            {whiskey.image ? (
              <img 
                src={whiskey.image} 
                alt={whiskey.name} 
                className="max-h-56 object-contain"
              />
            ) : (
              <div className="bg-gray-100 h-56 w-full flex items-center justify-center text-gray-400 rounded-md">
                No Image Available
              </div>
            )}
          </div>
        </div>
      </div>

      <Separator className="my-4" />

      {/* Review Details */}
      <div className="p-4">
        <h2 className="text-2xl font-bold mb-4 text-gray-900 border-b-2 border-gray-800 pb-2">Tasting Notes</h2>

        <div className="space-y-4">
          {/* Nose */}
          <Card>
            <CardContent className="p-0">
              <div className="grid grid-cols-1 md:grid-cols-8">
                <div className="md:col-span-1 bg-gray-800 text-white p-4 flex flex-col items-center justify-center">
                  <div className="text-lg font-bold">Nose</div>
                  <div className="text-3xl font-bold mt-1">{review.noseScore || '-'}</div>
                </div>
                <div className="md:col-span-2 bg-gray-100 p-4">
                  <h3 className={sectionTitleClass}>Profiles</h3>
                  <p>{formatAromasList(review.noseAromas)}</p>
                </div>
                <div className="md:col-span-5 p-4">
                  <h3 className={sectionTitleClass}>Description</h3>
                  <p>{formatNotes(review.noseNotes)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Mouthfeel */}
          <Card>
            <CardContent className="p-0">
              <div className="grid grid-cols-1 md:grid-cols-8">
                <div className="md:col-span-1 bg-gray-800 text-white p-4 flex flex-col items-center justify-center">
                  <div className="text-lg font-bold">Mouth Feel</div>
                  <div className="text-3xl font-bold mt-1">{review.mouthfeelScore || '-'}</div>
                </div>
                <div className="md:col-span-2 bg-gray-100 p-4">
                  <h3 className={sectionTitleClass}>Characteristics</h3>
                  <div className="space-y-1">
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Alcohol:</span> {review.mouthfeelAlcohol ? capitalizeFirstLetter(review.mouthfeelAlcohol) : '-'}</p>
                    </div>
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Viscosity:</span> {review.mouthfeelViscosity ? capitalizeFirstLetter(review.mouthfeelViscosity) : '-'}</p>
                    </div>
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Feel:</span> {review.mouthfeelPleasantness ? capitalizeFirstLetter(review.mouthfeelPleasantness) : '-'}</p>
                    </div>
                  </div>
                </div>
                <div className="md:col-span-5 p-4">
                  <h3 className={sectionTitleClass}>Description</h3>
                  <p>{formatNotes(review.mouthfeelNotes)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Taste */}
          <Card>
            <CardContent className="p-0">
              <div className="grid grid-cols-1 md:grid-cols-8">
                <div className="md:col-span-1 bg-gray-800 text-white p-4 flex flex-col items-center justify-center">
                  <div className="text-lg font-bold">Taste</div>
                  <div className="text-3xl font-bold mt-1">{review.tasteScore || '-'}</div>
                </div>
                <div className="md:col-span-2 bg-gray-100 p-4">
                  <h3 className={sectionTitleClass}>Profiles</h3>
                  <p>{formatAromasList(review.tasteFlavors)}</p>
                </div>
                <div className="md:col-span-5 p-4">
                  <h3 className={sectionTitleClass}>Description</h3>
                  <p>{formatNotes(review.tasteNotes)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Finish */}
          <Card>
            <CardContent className="p-0">
              <div className="grid grid-cols-1 md:grid-cols-8">
                <div className="md:col-span-1 bg-gray-800 text-white p-4 flex flex-col items-center justify-center">
                  <div className="text-lg font-bold">Finish</div>
                  <div className="text-3xl font-bold mt-1">{review.finishScore || '-'}</div>
                </div>
                <div className="md:col-span-2 bg-gray-100 p-4">
                  <h3 className={sectionTitleClass}>Profiles</h3>
                  <p>{formatAromasList(review.finishFlavors)}</p>
                  <div className="bg-gray-200 px-3 py-2 rounded-md mt-2">
                    <p><span className="font-medium text-gray-900">Length:</span> {review.finishLength ? capitalizeFirstLetter(review.finishLength) : '-'}</p>
                  </div>
                </div>
                <div className="md:col-span-5 p-4">
                  <h3 className={sectionTitleClass}>Description</h3>
                  <p>{formatNotes(review.finishNotes)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Value */}
          <Card>
            <CardContent className="p-0">
              <div className="grid grid-cols-1 md:grid-cols-8">
                <div className="md:col-span-1 bg-gray-800 text-white p-4 flex flex-col items-center justify-center">
                  <div className="text-lg font-bold">Value</div>
                  <div className="text-3xl font-bold mt-1">{review.valueScore || '-'}</div>
                </div>
                <div className="md:col-span-2 bg-gray-100 p-4">
                  <h3 className={sectionTitleClass}>Assessment</h3>
                  <div className="space-y-1">
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Availability:</span> {review.valueAvailability ? capitalizeFirstLetter(review.valueAvailability) : '-'}</p>
                    </div>
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Buy Again:</span> {review.valueBuyAgain ? capitalizeFirstLetter(review.valueBuyAgain) : '-'}</p>
                    </div>
                    <div className="bg-gray-200 px-3 py-2 rounded-md mb-1">
                      <p><span className="font-medium text-gray-900">Occasion:</span> {review.valueOccasion ? capitalizeFirstLetter(review.valueOccasion) : '-'}</p>
                    </div>
                  </div>
                </div>
                <div className="md:col-span-5 p-4">
                  <h3 className={sectionTitleClass}>Description</h3>
                  <p>{formatNotes(review.valueNotes)}</p>
                </div>
              </div>
            </CardContent>
          </Card>


        </div>
      </div>

      {/* Footer with review date */}
      <div className="bg-gray-100 p-4 text-center text-sm text-gray-500 rounded-b-lg">
        Review date: {review.date ? new Date(review.date).toLocaleDateString() : 'Unknown'}
      </div>
    </div>
  );
}

