import React from 'react';
import { useParams, useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { Whiskey, ReviewNote } from '@shared/schema';
import { ReviewDetailPage } from '@/components/ReviewDetailPage';
import { Header } from '@/components/Header';
import { Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface ReviewData {
  whiskey: Whiskey;
  review: ReviewNote;
}

export default function ReviewPage() {
  const [, setLocation] = useLocation();
  const params = useParams<{ id: string, reviewId: string }>();
  const whiskeyId = parseInt(params.id);
  const reviewId = params.reviewId;

  // For VERY DETAILED debugging, log everything related to our data
  console.log("=== REVIEW PAGE TRACING BEGINS ===");
  console.log("URL Parameters:", { id: params.id, reviewId: params.reviewId });
  console.log("Parsed IDs:", { whiskeyId, reviewId, whiskeyIdType: typeof whiskeyId, reviewIdType: typeof reviewId });

  // Use the dedicated endpoint to get both whiskey and review at once
  const { 
    data: reviewData,
    isLoading, 
    isError,
    error
  } = useQuery<ReviewData>({
    queryKey: [`/api/whiskeys/${whiskeyId}/reviews/${reviewId}`],
    enabled: !isNaN(whiskeyId) && !!reviewId,
    retry: 1,
  });

  // Log our query and results
  console.log("Query info:", {
    endpoint: `/api/whiskeys/${whiskeyId}/reviews/${reviewId}`,
    queryKey: [`/api/whiskeys/${whiskeyId}/reviews/${reviewId}`],
    isEnabled: !isNaN(whiskeyId) && !!reviewId,
    isLoading,
    isError
  });

  // Make an explicit fetch call just for debugging purposes
  React.useEffect(() => {
    if (!isNaN(whiskeyId) && reviewId) {
      const debugFetch = async () => {
        try {
          const response = await fetch(`/api/whiskeys/${whiskeyId}/reviews/${reviewId}`, {
            credentials: 'include'
          });
          console.log("DEBUG Fetch Status:", response.status);
          if (response.ok) {
            const data = await response.json();
            console.log("DEBUG Fetch Response:", data);
          } else {
            const errorText = await response.text();
            console.error("DEBUG Fetch Error:", errorText);
          }
        } catch (err) {
          console.error("DEBUG Fetch Exception:", err);
        }
      };
      debugFetch();
    }
  }, [whiskeyId, reviewId]);

  console.log("Review data from query:", reviewData);
  console.log("Error if any:", error);
  console.log("=== REVIEW PAGE TRACING ENDS ===");

  // Go back to the previous page
  const handleBack = () => {
    setLocation('/');
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto py-8 flex items-center justify-center">
          <Loader2 className="h-8 w-8 animate-spin text-amber-600" />
        </div>
      </div>
    );
  }

  if (isError || !reviewData) {
    console.error("Error loading review data:", error);

    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto py-8">
          <div className="bg-white rounded-lg shadow p-6 text-center">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Review Not Found</h2>
            <p className="text-gray-600 mb-6">
              The review you're looking for doesn't exist or has been removed.
            </p>
            <div className="mb-4 text-left p-4 bg-gray-50 rounded text-sm font-mono overflow-x-auto">
              <p>Whiskey ID: {whiskeyId}</p>
              <p>Review ID: {reviewId}</p>
              <p>Error: {error instanceof Error ? error.message : "Unknown error"}</p>
              <p>Query Key: {JSON.stringify([`/api/whiskeys/${whiskeyId}/reviews/${reviewId}`])}</p>
            </div>
            <Button onClick={handleBack}>Go Back</Button>
          </div>
        </div>
      </div>
    );
  }

  const { whiskey, review } = reviewData;

  // Extra validation to prevent downstream errors
  if (!whiskey || !review) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto py-8">
          <div className="bg-white rounded-lg shadow p-6 text-center">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Invalid Review Data</h2>
            <p className="text-gray-600 mb-6">
              The data received from the server was incomplete or invalid.
            </p>
            <div className="mb-4 text-left p-4 bg-gray-50 rounded text-sm font-mono overflow-x-auto">
              <p>Whiskey ID: {whiskeyId}</p>
              <p>Review ID: {reviewId}</p>
              <p>Whiskey data received: {whiskey ? 'Yes' : 'No'}</p>
              <p>Review data received: {review ? 'Yes' : 'No'}</p>
              <p>Raw data: {JSON.stringify(reviewData)}</p>
            </div>
            <Button onClick={handleBack}>Go Back</Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="container mx-auto py-8">
        <div className="mb-4">
          <Button onClick={handleBack} variant="outline">
            &larr; Back to Collection
          </Button>
        </div>

        <ReviewDetailPage whiskey={whiskey} review={review} />
      </div>
    </div>
  );
}

client/src/pages/SharedReview.tsx.content:
import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { useParams } from 'wouter';
import { getQueryFn } from '@/lib/queryClient';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { WhiskeyCategory } from '@/components/ui/whiskey-category';
import { Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Link } from 'wouter';
import ReviewLikes from '@/components/ReviewLikes';
import ReviewComments from '@/components/ReviewComments';
import { useAuth } from '@/hooks/use-auth';

interface SharedReviewData {
  whiskey: {
    id: number;
    name: string;
    distillery: string;
    type: string;
    age: number | null;
    price: number | null;
    abv: number | null;
    region: string | null;
    bottleType?: string | null;
    mashBill?: string | null;
    caskStrength?: boolean | null;
    imageUrl?: string | null;
  };
  review: {
    id: string;
    rating: number;
    date: string;
    text: string;
    flavor: string;
    visual?: string;
    nose?: string;
    mouthfeel?: string;
    taste?: string;
    finish?: string;
    value?: string;
  };
  user: {
    id: number;
    username: string;
  };
}

const SharedReview = () => {
  const { shareId } = useParams();
  const { data, isLoading, error } = useQuery<SharedReviewData>({
    queryKey: [`/api/shared/${shareId}`],
    queryFn: getQueryFn({ on401: 'returnNull' }),
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="h-8 w-8 animate-spin text-border" />
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <Card className="border-red-200 bg-red-50">
          <CardHeader>
            <CardTitle className="text-red-700">Review Not Found</CardTitle>
            <CardDescription className="text-red-600">
              This review may have been removed or made private by the author.
            </CardDescription>
          </CardHeader>
          <CardFooter>
            <Button asChild>
              <Link to="/">Go to Homepage</Link>
            </Button>
          </CardFooter>
        </Card>
      </div>
    );
  }

  const { whiskey, review, user } = data;

  // Extract overall rating
  const overallRating = review.rating;

  // Function to format review text for display
  const formatReviewText = (text: string) => {
    // Replace section headers with styled headers
    const formattedText = text
      .replace(/## (.*?) ##/g, '<h3 class="text-lg font-bold mt-4 mb-2 text-amber-800">$1</h3>')
      .replace(/\n\n/g, '<br><br>')
      .replace(/\n/g, '<br>');

    return <div dangerouslySetInnerHTML={{ __html: formattedText }} />;
  };

  const { user: currentUser } = useAuth();

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <Card className="mb-8">
        <CardHeader className="pb-2">
          <div className="flex justify-between items-start">
            <div>
              <div className="flex items-center gap-2 mb-1">
                <WhiskeyCategory type={whiskey.type} />
                <span className="text-sm text-muted-foreground">Shared by {user.username}</span>
              </div>
              <CardTitle className="text-2xl">{whiskey.name}</CardTitle>
              <CardDescription>
                {whiskey.distillery}
                {whiskey.age && ` • ${whiskey.age} years`}
                {whiskey.abv && ` • ${whiskey.abv}% ABV`}
                {whiskey.region && ` • ${whiskey.region}`}
              </CardDescription>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-amber-600">{overallRating.toFixed(1)}</div>
              <div className="text-xs text-muted-foreground">Rating</div>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="mt-4 space-y-4">
            {whiskey.imageUrl && (
              <img
                src={whiskey.imageUrl}
                alt={whiskey.name}
                className="w-full max-h-64 object-contain rounded-md"
              />
            )}
            <div className="mt-6 prose prose-amber max-w-none text-sm">
              {formatReviewText(review.text)}
            </div>
          </div>

          {/* Review Actions */}
          <div className="mt-8 pt-4 border-t flex items-center justify-between">
            <ReviewLikes whiskey={whiskey} review={review} />

            <div className="text-sm text-muted-foreground">
              Reviewed on {new Date(review.date).toLocaleDateString()}
            </div>
          </div>
        </CardContent>
        <CardFooter className="flex justify-between border-t pt-4">
          <Button asChild variant="outline">
            <Link to="/">Back to Collection</Link>
          </Button>
        </CardFooter>
      </Card>

      {/* Comments Section */}
      <Card className="mb-8">
        <CardContent className="pt-6">
          <ReviewComments whiskey={whiskey} review={review} />
        </CardContent>
      </Card>
    </div>
  );
};

export default SharedReview;

client/src/pages/auth-page.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Redirect } from "wouter";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Loader2 } from "lucide-react";

const loginSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

const registerSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  displayName: z.string().min(2, "Display name must be at least 2 characters"),
});

type LoginFormValues = z.infer<typeof loginSchema>;
type RegisterFormValues = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const { user, loginMutation, registerMutation } = useAuth();
  const [activeTab, setActiveTab] = useState<string>("login");

  const loginForm = useForm<LoginFormValues>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const registerForm = useForm<RegisterFormValues>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: "",
      password: "",
      displayName: "",
    },
  });

  const onLogin = (data: LoginFormValues) => {
    loginMutation.mutate(data);
  };

  const onRegister = (data: RegisterFormValues) => {
    registerMutation.mutate(data);
  };

  // If already logged in, redirect to home
  if (user) {
    return <Redirect to="/" />;
  }

  return (
    <div className="min-h-screen flex flex-col md:flex-row">
      {/* Auth form section */}
      <div className="flex-1 flex items-center justify-center p-6 md:p-10">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle className="text-2xl font-bold text-center">
              <span className="bg-gradient-to-r from-amber-700 to-amber-500 bg-clip-text text-transparent">
                WhiskeyPedia
              </span>
            </CardTitle>
            <CardDescription className="text-center">
              Log in to manage your personal whiskey collection
            </CardDescription>
          </CardHeader>

          <Tabs defaultValue="login" value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="login">Login</TabsTrigger>
              <TabsTrigger value="register">Register</TabsTrigger>
            </TabsList>

            <TabsContent value="login">
              <Form {...loginForm}>
                <form onSubmit={loginForm.handleSubmit(onLogin)}>
                  <CardContent className="space-y-4 pt-4">
                    <FormField
                      control={loginForm.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username</FormLabel>
                          <FormControl>
                            <Input placeholder="Enter your username" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={loginForm.control}
                      name="password"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Password</FormLabel>
                          <FormControl>
                            <Input type="password" placeholder="Enter your password" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </CardContent>

                  <CardFooter>
                    <Button 
                      type="submit" 
                      className="w-full bg-gradient-to-r from-amber-700 to-amber-500 hover:from-amber-800 hover:to-amber-600"
                      disabled={loginMutation.isPending}
                    >
                      {loginMutation.isPending ? (
                        <>
                          <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                          Logging in...
                        </>
                      ) : (
                        "Login"
                      )}
                    </Button>
                  </CardFooter>
                </form>
              </Form>
            </TabsContent>

            <TabsContent value="register">
              <Form {...registerForm}>
                <form onSubmit={registerForm.handleSubmit(onRegister)}>
                  <CardContent className="space-y-4 pt-4">
                    <FormField
                      control={registerForm.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username</FormLabel>
                          <FormControl>
                            <Input placeholder="Choose a username" {...field} />
                          </FormControl>
                          <FormDescription>
                            This will be your login name
                          </FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={registerForm.control}
                      name="displayName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Display Name</FormLabel>
                          <FormControl>
                            <Input placeholder="Your name to display" {...field} />
                          </FormControl>
                          <FormDescription>
                            This name will be shown in the interface
                          </FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={registerForm.control}
                      name="password"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Password</FormLabel>
                          <FormControl>
                            <Input type="password" placeholder="Choose a password" {...field} />
                          </FormControl>
                          <FormDescription>
                            At least 6 characters
                          </FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </CardContent>

                  <CardFooter>
                    <Button 
                      type="submit" 
